#pragma once// device has default bus address of 0x76#define ADDR _u(0x76)// hardware registers#define REG_CONFIG _u(0xF5)#define REG_CTRL_MEAS _u(0xF4)#define REG_CTRL_HUM _u(0xF2)#define REG_RESET _u(0xE0)#define REG_TEMP_XLSB _u(0xFC)#define REG_TEMP_LSB _u(0xFB)#define REG_TEMP_MSB _u(0xFA)#define REG_PRESSURE_XLSB _u(0xF9)#define REG_PRESSURE_LSB _u(0xF8)#define REG_PRESSURE_MSB _u(0xF7)#define REG_HUM_LSB _u(0xFE)#define REG_HUM_MSB _u(0xFD)// calibration registers temp#define REG_DIG_T1 _u(0x88) //unsigned int16#define REG_DIG_T2 _u(0x8A)#define REG_DIG_T3 _u(0x8C)// calibration registers press#define REG_DIG_P1 _u(0x8E) //unsigned int16#define REG_DIG_P2 _u(0x90)#define REG_DIG_P3 _u(0x92)#define REG_DIG_P4 _u(0x94)#define REG_DIG_P5 _u(0x96)#define REG_DIG_P6 _u(0x98)#define REG_DIG_P7 _u(0x9A)#define REG_DIG_P8 _u(0x9C)#define REG_DIG_P9 _u(0x9E)//#define REG_DIG_H1 _u(0xA1) //unsigned int8#define REG_DIG_H2 _u(0xE1)#define REG_DIG_H3 _u(0xE3) //unsigned int8#define REG_DIG_H4 _u(0xE4)#define REG_DIG_H5 _u(0xE5)#define REG_DIG_H6 _u(0xE7) //int 8// number of calibration registers to be read#define NUM_CALIB_PARAMS_TEMP 6#define NUM_CALIB_PARAMS_PRES 18#define NUM_CALIB_PARAMS_HUM 9