#include "interrupt.h"void LoraCallback(uint gpio, uint32_t events){  uint8_t irqFlag = Lora.getIRQFlags();  if ((irqFlag & IRQ_RXDONE_MASK) && ((irqFlag & IRQ_CRCERR_MASK) == 0)){    auto pack= Lora.receiveIRQ();#ifdef DEBUG    printf("pack: %s\n", pack.getPayload());#endif  }  Lora.clearIRQ();  busy_wait_ms(300);  return;}void INTERRUPT::LoraInterruptSetup(uint8_t dio) {  gpio_init(dio);  gpio_set_dir(dio, GPIO_IN);  gpio_pull_up(dio);  gpio_set_irq_enabled_with_callback(dio, GPIO_IRQ_EDGE_RISE, true, LoraCallback);}