#include "PCF8574.h"void PCF8574::PCF8574::write(uint8_t data) {    uint8_t src[1] = {data};    i2c_write_blocking(this->_i2c, this->_addr, src, 1, false);}uint8_t PCF8574::PCF8574::read() {  uint8_t data = 0;  i2c_read_blocking(this->_i2c, this->_addr, &data, 1, false);#ifdef DEBUG  printf("data: %d\n", data);#endif  return data;}PCF8574::PCF8574::PCF8574(i2c_inst_t* i2c,  uint8_t sda, uint8_t scl,                          uint8_t addr) : _i2c(i2c), _addr(addr), _sda(sda), _scl(scl){  i2c_init(this->_i2c, 400*1000);  gpio_set_function(this->_scl, GPIO_FUNC_I2C);  gpio_set_function(this->_sda, GPIO_FUNC_I2C);//  if(config)//    configure(config);}